ticfunction cnt = stevilo_sedel(M)   [m, n] = size(M);   stevec = 0;   for i = 1:1:m       for j = 1:1:n           if ali_je_sedlo(M, i, j)                stevec = stevec + 1;           end       end    end    cnt = stevec;endfunctiontoc%function cnt = stevilo_sedel2(M)%  cnt = sum(sum(min(M, [], 2) == M & M == max(M, [], 1))) ...  %         + sum(sum(max(M, [], 2) == M & M == min(M, [], 1)));%endfunction