podatki = dlmread('kolokviji.csv', ',')%uvozili smo datoteko kolokviji.csv,kjer je mejni znak dilimiter(vejica)vsota = sum(podatki, 2) %izraèunamo vsote vrstic tabele ocenepovprecje = mean(podatki, 2) %izraèunamo povpreèja po vrsticahocene = izracunaj_oceno(povprecje) %iz stolpca s povpreèji izraèuna stolpec ocenzaporedje = (1:length(ocene))' %števila od 1 do n - n je dolzina seznama ocenerezultat = [zaporedje, podatki, vsota, povprecje, ocene] %matrikadlmwrite('rezultat.csv', rezultat, ',')disp('Tip rezultata')class(rezultat)disp('Tipi stolpcev')class(podatki)class(vsota)class(povprecje)class(zaporedje)class(ocene)%pri povpreèju niso samo cela števila, zato ga zaokrožimopovprecje = round(mean(podatki, 2))ocene = izracunaj_oceno(povprecje)rezultat = [zaporedje, podatki, vsota, povprecje, ocene]dlmwrite('rezultat_celi.csv', rezultat, ',')pkg install -forge iopkg install -forge statisticspkg load statistics% alternativa 1frekvence = tabulate(ocene)% alternativa 2izracunaj = @(n) length(ocene(ocene==n))arrayfun(izracunaj, 6:10)  % 'map' za matlab% alternativa 3frekvence = zeros(1, 5)for n = 6:10  frekvence(n-5) = length(ocene(ocene==n))endclass(frekvence)bar(6:10, frekvence(6:10, 3))%stolpièni diagram, na x-osi med 6 in 10, na y-osi ustrezne komponente iz vektorja frekvenceslika = figure()bar(6:10, frekvence(6:10, 3), 'facecolor', 'r', 'edgecolor', 'b')%r kot red, b kot black %facecolor je barvna površina polja, %edgecolor je barva obrobetitle('Porazdelitev ocen kolokvija') %dodamo naslovxlabel ("Ocene"); %na x os dodamo napis Oceneylabel ("Stevilo studentov"); %na y os dodamo napis Stevilo studentovtext (10, 10, "Bravo!"); %na koodrinati (10, 10) dodamo napis Bravo!print(slika, 'slika.pdf') %rezultat smo shranili v pdf